import numpy as np
import pandas as pd
import matplotlib.pyplot as plt
import seaborn as sns
import lime
from lime import lime_tabular
import datetime



import warnings
warnings.filterwarnings('ignore')


# In[2]:


train=pd.read_csv("Training.csv")
test=pd.read_csv("Testing.csv")


# In[6]:


train=train.drop(["Unnamed: 133"],axis=1)


# In[17]:


P = train[["prognosis"]]
X = train.drop(["prognosis"],axis=1)
Y = test.drop(["prognosis"],axis=1)


# In[18]:


from sklearn.model_selection import train_test_split
xtrain,xtest,ytrain,ytest = train_test_split(X,P,test_size=0.2,random_state=42)


# In[19]:


from sklearn.ensemble import RandomForestClassifier
from sklearn.metrics import accuracy_score


# In[20]:


rf= RandomForestClassifier(random_state=42)
model_rf = rf.fit(xtrain,ytrain)
tr_pred_rf = model_rf.predict(xtrain)
ts_pred_rf = model_rf.predict(xtest)


# In[21]:


def predict_disease(model, attributes):
    # Create a DataFrame with the input attributes
    input_data = pd.DataFrame([attributes], columns=X.columns)
    
    # Make predictions
    predicted_disease = model.predict(input_data)
    
    return predicted_disease[0]  # Return the predicted disease name


