import datetime

def append_lime_explanation_to_html(model, data_row,user_input,xai_response):
    # Generate Lime explanation
    explainer = lime_tabular.LimeTabularExplainer(xtrain.values, feature_names=X.columns, class_names=['prognosis'], discretize_continuous=True)
    exp = explainer.explain_instance(np.array(data_row), model.predict_proba, num_features=len(X.columns))
    html_explanation = exp.as_html()
    
    # Append Lime explanation to HTML file
    timestamp = datetime.datetime.now().strftime("%Y-%m-%d %H:%M:%S")
    with open("lime_explanations.html", "a", encoding="utf-8") as f:
        f.write(f"<div><strong>User: </strong>{user_input}</div>")
        f.write(f"<div><strong>XAI: </strong>{xai_response}</div>")
        f.write(f"<div><strong>Time:</strong> {timestamp}</div>")
        f.write(html_explanation)
        f.write("<hr>")  # Add a horizontal line for separation
    with open("conversation_log.txt", "a") as g:
        timestamp = datetime.datetime.now().strftime("%Y-%m-%d %H:%M:%S")
        g.write(f"Time: {timestamp}\n")
        g.write(f"User: {user_input}\n")
        g.write(f"XAI: {xai_response}\n\n")

